//Hello World in C#
class HelloWorld
{
     static void Main()
    {
        System.Console.WriteLine(\"Hello, World!\");
    }
}

// Hello World in C++ (pre-ISO)

#include <iostream.h>
main()
{
    cout << \"Hello World!\" << endl;
    return 0;
}

// Hello World in Java
class HelloWorld
{
    static public void main( String args[] )
{
        System.out.println( \"Hello World!\" );}
}
